body
{
	color:#000;
	width:100%;
	cursor:default;
	font-family:Times,"Times New Roman",serif;
	font-size:.7em;
	background-color: #000022;
	background-image:url('data:image/svg+xml;base64,');
	height:100%;
}
/* landing page info */

div#login {
	top:10%;
	position: relative;
	width:20%;
	margin:auto;
	padding-left:3%;
	padding-top:1%;
	display:block;
	font-size:1.5em;
	font-family:Arial, sans-serif;
	border: 1px black solid;
	border-radius:15px;
	background-color:#432;
	color:#fff;
	box-shadow: 0px 0px 50px #FFF;
	;
}

input {
display:block;
width:80%;
}

H1
{
	color:#000; font-size:1.2em; font-family:AquilineRegular, sans-serif;
	line-height:50%;
	font-weight:bold;
	text-align:left;
}
H2 {	
	color: #000000;
	font-family:sans-serif;
	font-size:1em;
	line-height:25%;
}
ul {	list-style-type:none;
	padding:0px;
	margin:0px;	}
li {	list-style-type:none;
	padding:0px;
	margin:0px;	}
a:link 	{color:#FFF; font-weight:bold; font-size:1.5em; font-family:sans-serif;}
a:visited {color:#030;}
a:hover {color:#484; font-family:monospace;}
a:active {color:#F00; font-family:monospace;}
